@import url('https://fonts.googleapis.com/css2?family=Pacifico&family=Sixtyfour+Convergence&family=Sigmar&family=Kanit:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&family=Cormorant+Garamond:ital,wght@0,300;0,400;0,500;0,600;0,700;1,300;1,400;1,500;1,600;1,700&family=Montserrat:ital,wght@0,100..900;1,100..900&display=swap');

/* --- Available Fonts & Fallbacks --- */
/* 'Pacifico', cursive; */
/* 'Sixtyfour Convergence', system-ui; */
/* 'Sigmar', sans-serif; */
/* 'Kanit', sans-serif; */
/* 'Cormorant Garamond', serif; */
/* 'Montserrat', sans-serif; */


h1, h2, h3, h4, h5, h6 {
    font-family: 'Cormorant Garamond', serif;
    font-weight: 300;
}

h1 {
    letter-spacing: 0.05em;
}

.primaryColors {
    @apply stonePrimary;
} 

.secondaryColors {
    @apply graySecondary;
} 

.neutralColors{
    @apply slateNeutral;
}

.background-section {
  background-color: var(--color-neutral-100);
}

.dark .background-section {
  background-color: var(--color-neutral-950);
}

.background-card {
  background-color: var(--color-neutral-200);
}

.dark .background-card {
  background-color: var(--color-neutral-900);
}

p, label {
  color: var(--color-neutral-600);
  font-family: 'Montserrat', sans-serif;

}

.dark p, .dark label {
  color: var(--color-neutral-400);
}

:host a {
  color: var(--color-neutral-700);
  font-family: 'Montserrat', sans-serif;
}

:host a:hover {
  color: var(--color-neutral-800);
}

.dark a {
  color: var(--color-neutral-300);
}

.dark a:hover {
  color: var(--color-neutral-200);
}

:host button {
  font-family: 'Montserrat', sans-serif;
  background-color: var(--color-primary-400);
  color: var(--color-neutral-100);
}

:host button:hover {
  background-color: var(--color-primary-500);
  color: var(--color-neutral-50);
}

.dark button {
  background-color: var(--color-primary-700);
  color: var(--color-neutral-300);
}

.dark button:hover {
  background-color: var(--color-primary-800);
  color: var(--color-neutral-200);
}

h1 {
  color: var(--color-neutral-800);
}

.dark h1 {
  color: var(--color-neutral-100);
}

h2, h3, h4, h5, h6 {
  color: var(--color-neutral-700);
}

.dark h2,
.dark h3,
.dark h4,
.dark h5,
.dark h6 {
  color: var(--color-neutral-200);
}

h1 {
    letter-spacing: 0.05em;
}

.theme-switcher {
  transition: all 0.3s ease-in-out;
  border-radius: 9999px; 
  padding: 0.5rem; 
}

.theme-switcher-theme{
  color: var(--color-primary-700);
}

.theme-switcher-theme:hover{
  color: var(--color-primary-900);
}

.dark .theme-switcher-theme{
  color: var(--color-primary-200);
}

.dark .theme-switcher-theme:hover{
  color: var(--color-primary-50);
}

.language-button {
  display: inline-flex;
  justify-content: center;
  padding: 0.625rem; 
  border-radius: 9999px; 
  cursor: pointer;
}

.language-button-theme {
  background-color: transparent;
  color: var(--color-primary-500);
  border-color: transparent;
}

.dark .language-button-theme {
  background-color: transparent;
  color: var(--color-primary-900);
  border-color: transparent;
}

.language-button-icon {
  height: 1.25rem; 
  width: 1.25rem; 
  border-radius: 9999px; 
}

.language-menu {
  z-index: 100; 
  margin-top: 1rem; 
  margin-bottom: 1rem; 
  font-size: 1rem; 
  line-height: 1.5rem; 
  list-style: none; 
  border-radius: 0.25rem; 
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1); 
}

.language-menu-theme {
  background-color: var(--color-primary-500);
}

.dark .language-menu-theme {
  background-color: var(--color-primary-950);
}

.language-menu-link {
  display: flex; 
  align-items: center; 
  padding-top: 0.5rem; 
  padding-bottom: 0.5rem; 
  padding-left: 1rem; 
  padding-right: 1rem; 
  margin: 0.25rem; 
  border-radius: 0.25rem; 
  text-align: left; 
  cursor: pointer;
}

.language-menu-link-theme {
  color: var(--color-primary-50);
}

.dark .language-menu-link-theme {
  color: var(--color-primary-100);
}

.language-menu-link-theme:hover {
  background-color: var(--color-primary-600);
  color: var(--color-primary-100);
}

.dark .language-menu-link-theme:hover {
  background-color: var(--color-primary-500);
  color: var(--color-primary-50);
}

.language-menu-icon {
  display: inline-flex;
  align-items: center;
  height: 0.875rem; 
  width: 0.875rem; 
  border-radius: 9999px; 
  margin-top: 0.25rem; 
  margin-right: 0.5rem; 
}

.language-menu-text {
  font-size: 0.875rem; 
  line-height: 1.25rem; 
  font-weight: 600; 
}

.modal {
    --modal-bg: #F8FAFC;
    --modal-bg-dark: #334155;
    --modal-btn: #777777;
    --modal-btn-hover: #1f2733;
    --modal-btn-dark:#aaaaaa;
    --modal-btn-dark-hover:#FFFFFF;
}

